import React, { Component } from 'react';
import { getMovies, count } from '../services/fakeMovieService';

class Movies extends Component {
  state = {
    movies: getMovies(),
    counter: count,
  };

  handleDelete = (movie, counter) => {
    const movies = this.state.movies.filter((m) => m._id !== movie._id);
    const c = counter - 1;
    this.setState({ movies, counter });
  };

  render() {
    return (
      <div>
        <h4>Showing {this.counter} movies in Database</h4>
        <table className="table">
          <thead>
            <tr>
              <th>Title</th>
              <th>Genre</th>
              <th>Stock</th>
              <th>Rate</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {this.state.movies.map((movie) => (
              <tr key={movie._id}>
                <th>{movie.title}</th>
                <th>{movie.genre.name}</th>
                <th>{movie.numberInStock}</th>
                <th>{movie.dailyRentalRate}</th>
                <th>
                  <button
                    className="btn btn-danger"
                    onClick={() => this.handleDelete(movie)}
                  >
                    Delete
                  </button>
                </th>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Movies;
